import React,{Component} from 'react';
import Clock from './Clock'

class App extends Component{
    constructor(){
        super()
        this.state = {
            days: 0,
            minutes: 0,
            hours: 0,
            seconds: 0,
            time_up: ""
        }
        this.x = null
        this.deadline = null
    }
    count() {
        var now = new Date().getTime();
        var t = this.deadline - now;
        var days = Math.floor(t/(1000*60*60*24));
        var hours = Math.floor((t%(1000*60*60*24))/(1000*60*60));
        var minutes = Math.floor((t % (1000 * 60 * 60)) / (1000 * 60));
        var seconds = Math.floor((t % (1000 * 60)) / 1000);
        this.setState({days, minutes, hours, seconds})
        if (t < 0) {
                clearInterval(this.x);
                this.setState({ days: 0, minutes: 0, hours: 0, seconds: 0, time_up: "TIME IS UP" })
            }
        }
    componentDidMount() {
        this.deadline = new Date("apr 29, 2018 21:00:00").getTime();

        this.x = setInterval(this.count, 1000);
    }
    render() {
        const { days, seconds, hours, minutes, time_up } = this.state
        return (
            <div className="DottedBox">
                <Clock className="DottedBox_content"  
                days = {days}
                hours = {hours}
                minutes = {minutes}
                seconds = {seconds}
                time_up = {time_up}
                />
            </div>
        )
    }
}

export default App
